mapper.select:
select 	userId, userPw, userNm, regDate
from 	tbl_member
where 	userId = #{userId}

mapper.update:
update 	tbl_member
set 	userNm = #{userNm}
where 	userId = #{userId}

update 	tbl_member
set 	userPw = #{userPw}
where 	userId = #{userId}

mapper.select:
select 	userId, userNm
from 	tbl_member
where 	userId = #{userId}
and 	userPw = #{userPw}
